mapper.select:
	select * from 
  		(select rownum as rnum, q.* from 
      		(select 
          		question_no, question_title, question_writer, reg_date 
     			 from question order by question_no desc
     			 )q
     		)
	where rnum between #{start} and #{end}


	select * 
  from (
      select rownum AS rnum, q.*
      from (
          select question_no, question_title, question_writer, reg_date
          from question
          where question_writer like '%'||#{search}||'%'
          order by question_no desc
      ) q
  )
  WHERE rnum BETWEEN #{start} AND #{end}

mapper.insert:
insert into question_comment values( question_comment_seq.nextval, #{questionCommentWriter}, #{questionCommentContent}, to_char(sysdate, 'yyyy-mm-dd'), #{questionRef},
null
#{questionCommentRef}
)

mapper.update:
update question_comment set 
	question_comment_content = #{questionCommentContent}
where
	question_comment_no = #{questionCommentNo}

update question set
	question_title = #{questionTitle},
	question_content = #{questionContent}
where
	question_no=#{questionNo}

mapper.select:
	select * 
  from (
      select rownum AS rnum, q.*
      from (
          select question_no, question_title, question_writer, reg_date
